function randColor(){return"#"+Math.floor(Math.random()*16777215).toString(16).padStart(6,"0")}function adjustBrightness(hex,amt){let col=parseInt(hex.slice(1),16),r=(col>>16)+amt,g=(col>>8&255)+amt,b=(col&255)+amt;return"#"+((1<<24)+(r<0?0:r>255?255:r<<16)+(g<0?0:g>255?255:g<<8)+(b<0?0:b>255?255:b)).toString(16).slice(1)}function randGradient(){let a=randColor(),b=adjustBrightness(a,Math.random()>.5?80:-80);return`linear-gradient(90deg, ${a}, ${b})`}function generateGradientMap(cats){const map={};cats.forEach(cat=>map[cat]=randGradient());return map}window.generateGradientMap=generateGradientMap;
