function randColor(){return"#"+Math.floor(Math.random()*16777215).toString(16).padStart(6,"0")}function randGradient(){let a=randColor(),b=randColor();return`linear-gradient(90deg, ${a}, ${b})`}function generateGradientMap(cats){const map={};cats.forEach(cat=>map[cat]=randGradient());return map}window.generateGradientMap=generateGradientMap;
